import { ChangeEvent, useRef, useState } from "react";
import st from "./Regist.module.scss";

interface AddToList {
  en?: string;
  ko?: string;
}

const Regist = () => {
  const enInput = useRef<HTMLInputElement>(null);
  const [wordArr, setWordArr] = useState<AddToList[]>([]);
  const [word, setWord] = useState<AddToList>({
    en: "",
    ko: "",
  });

  const AddToList = (newObj: AddToList) => {
    wordArr && setWordArr((prev) => prev?.concat(newObj));
  };

  // const registVoca = async () => {
  //   try {
  //     await fetch("http://localhost:8000/regist", {
  //       method: "POST",
  //       body: JSON.stringify({
  //         id: "id test",
  //         title: "title test",
  //         chapter: "chapter test",
  //       }),
  //     });
  //   } catch (error) {
  //     console.log(error);
  //     alert("Fail");
  //   }
  // };

  console.log(wordArr);

  return (
    <section className={st.regist_container}>
      <div className={st.form_container}>
        <div className={st.input_container}>
          <input
            type="text"
            placeholder="EN"
            ref={enInput}
            onChange={(event: ChangeEvent<HTMLInputElement>) => {
              const inputValue = event.currentTarget.value;
              setWord((prev) => ({
                ...prev,
                en: inputValue,
              }));
            }}
          />
          <input
            type="text"
            placeholder="KO"
            onChange={(event: ChangeEvent<HTMLInputElement>) => {
              const inputValue = event.currentTarget.value;
              setWord((prev) => ({
                ...prev,
                ko: inputValue,
              }));
            }}
          />
        </div>
        <button onClick={() => AddToList(word)}>Add</button>
      </div>
      <table>
        <th>
          <td></td>
          <td></td>
        </th>
        <tr>
          <td></td>
          <td></td>
        </tr>
      </table>
      <button>Submit</button>
    </section>
  );
};

export default Regist;
